function search(){const searchItem=document.getElementById("Product").value.toLowerCase();images=document.querySelectorAll(".menu_cat_con");let found=!1;images.forEach(image=>{const keywords=image.getAttribute("data-keywords").toLowerCase();keywords.includes(searchItem)?(image.style.display="block",found=!0):image.style.display="none"});let notfound=document.getElementById("product_not_found");notfound.style.display=found?"none":"flex"}for(var menuList=document.getElementById("menu_list"),btns=menuList.getElementsByClassName("menubar_function"),i=0;i<btns.length;i++)btns[i].addEventListener("click",(function(){var current=document.getElementsByClassName("active_menu");current[0].className=current[0].className.replace(" active_menu",""),this.className+=" active_menu"}));